import React, { useState } from "react";
import CheckoutButton from "./CheckoutButton";

export default function ShirtDetailPage({ selectedShirt, setPage, cart, setCart }) {
  const [size, setSize] = useState("S");
  const [quantity, setQuantity] = useState(1);

  const addToCart = () => {
    setCart([...cart, { ...selectedShirt, size, quantity }]);
    setPage("cart");
  };

  return (
    <div>
      <h2>{selectedShirt.name}</h2>
      <img src={selectedShirt.image} alt={selectedShirt.name} className="responsive" />
      <p>Price: ${selectedShirt.price}</p>

      <div className="form-group">
        <label>Size:</label>
        <select value={size} onChange={(e) => setSize(e.target.value)}>
          <option>S</option>
          <option>M</option>
          <option>L</option>
          <option>XL</option>
        </select>
      </div>

      <div className="form-group">
        <label>Quantity:</label>
        <input type="number" min="1" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} />
      </div>

      <button className="button" onClick={addToCart}>Add to Cart</button>
      <CheckoutButton 
        item={selectedShirt.name} 
        amount={selectedShirt.price} 
        size={size}
        quantity={quantity}
        seller={selectedShirt.seller}
      />
      <button className="button-alt" onClick={() => setPage("shop")}>Back to Shop</button>
    </div>
  );
}
