import React from "react";
import CartCheckoutButton from "./CartCheckoutButton"; // import the button

export default function CartPage({ cart, setCart, setPage }) {
  const removeItem = (index) => setCart(cart.filter((_, i) => i !== index));

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div>
      <h1>Your Cart</h1>
      {cart.length === 0 && <p>Your cart is empty!</p>}
      {cart.map((item, idx) => (
        <div key={idx} className="cart-item">
          <div className="cart-item-info">
            <img src={item.image} alt={item.name} />
            <div className="cart-item-text">
              <h3>{item.name}</h3>
              <p>Size: {item.size}</p>
              <p>Quantity: {item.quantity}</p>
            </div>
          </div>
          <div>
            <p className="cart-item-price">${item.price * item.quantity}</p>
            <button className="remove-btn" onClick={() => removeItem(idx)}>x</button>
          </div>
        </div>
      ))}
      <div className="checkout-summary">
        <p>Total:</p>
        <span>${total}</span>
      </div>

      {/* Checkout Button */}
      {cart.length > 0 && <CartCheckoutButton cart={cart} />}
    </div>
  );
}
