import React, { useState } from "react";

export default function CheckoutPage({ cart, setCart, setPage }) {
  const [dorm, setDorm] = useState("University Villas");
  const [customSpot, setCustomSpot] = useState("");

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleCheckout = async () => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_BACKEND_URL}/create-checkout-session`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ cart, dorm, customSpot }),
        }
      );
      const data = await response.json();
      if (data.url) {
        window.location.href = data.url; // redirect to Stripe
      } else {
        console.error("No checkout URL returned", data);
        alert("Checkout failed.");
      }
    } catch (err) {
      console.error("Checkout fetch error:", err);
      alert("Checkout request failed: " + err.message);
    }
  };

  return (
    <div>
      <h1>Checkout</h1>
      <div className="form-group">
        <label>Pickup Dorm / Spot:</label>
        <select value={dorm} onChange={(e) => setDorm(e.target.value)}>
          {/* your dorm options */}
        </select>
        {dorm === "Other" && (
          <input
            type="text"
            placeholder="Enter custom meetup spot"
            value={customSpot}
            onChange={(e) => setCustomSpot(e.target.value)}
          />
        )}
      </div>
      <div className="checkout-summary">
        <p>Total:</p>
        <span>${total}</span>
      </div>
      <button className="button" onClick={handleCheckout}>
        Pay with Card
      </button>
    </div>
  );
}
