import React, { useState } from "react";

const dorms = [
  "University Villas",
  "Campisi Hall",
  "Casa Italiana Hall",
  "Dunne Hall",
  "Finn Hall",
  "Graham Hall",
  "McLaughlin-Walsh Hall",
  "Nobili Hall",
  "Sanfilippo Hall",
  "Sobrato Hall",
  "Swig Hall",
  "Other",
];

export default function CheckoutPage({ cart, setCart, setPage }) {
  const [dorm, setDorm] = useState(dorms[0]);
  const [customSpot, setCustomSpot] = useState("");
  const [confirmed, setConfirmed] = useState(false);

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleCheckout = () => setConfirmed(true);

  return (
    <div>
      <h1>Checkout</h1>
      <div className="form-group">
        <label>Pickup Dorm / Spot:</label>
        <select value={dorm} onChange={(e) => setDorm(e.target.value)}>
          {dorms.map((d, i) => <option key={i}>{d}</option>)}
        </select>
        {dorm === "Other" && (
          <input type="text" placeholder="Enter custom meetup spot" value={customSpot} onChange={(e) => setCustomSpot(e.target.value)} />
        )}
      </div>
      <div className="checkout-summary">
        <p>Total:</p>
        <span>${total}</span>
      </div>
      {!confirmed ? (
        <button className="button" onClick={handleCheckout}>Confirm Order</button>
      ) : (
        <div>
          <h2>Order Confirmed!</h2>
          {cart.map((item, idx) => (
            <p key={idx}>Seller: {item.seller} â€” Contact to arrange meetup</p>
          ))}
          <p>Confirmation email sent to you (mock)</p>
          <button className="button-alt" onClick={() => { setCart([]); setPage("shop"); }}>Back to Shop</button>
        </div>
      )}
    </div>
  );
}
