import React from "react";

const shirts = [
  { id: 1, name: "Shirt 1", price: 35, image: "/src/images/shirt1.jpeg", seller: "Seller 1" },
  { id: 2, name: "Shirt 2", price: 35, image: "/images/shirt2.jpeg", seller: "Seller 2" },
  { id: 3, name: "Shirt 3", price: 35, image: "/images/shirt3.jpeg", seller: "Seller 3" },
  { id: 4, name: "Shirt 4", price: 35, image: "/images/shirt4.jpeg", seller: "Seller 4" },
  { id: 5, name: "Shirt 5", price: 35, image: "/images/shirt5.jpeg", seller: "Seller 5" },
  { id: 6, name: "Shirt 6", price: 35, image: "/images/shirt6.jpeg", seller: "Seller 6" },
  { id: 7, name: "Shirt 7", price: 35, image: "/images/shirt7.jpeg", seller: "Seller 7" },
  { id: 8, name: "Shirt 8", price: 35, image: "/images/shirt8.jpeg", seller: "Seller 8" },
  { id: 9, name: "Shirt 9", price: 35, image: "/images/shirt9.jpeg", seller: "Seller 9" },
  { id: 10, name: "Shirt 10", price: 35, image: "/images/shirt10.jpeg", seller: "Seller 10" },
];

const fullShirts = Array.from({ length: 24 }, (_, i) => shirts[i % 10]);

export default function ShopPage({ setPage, setSelectedShirt }) {
  return (
    <div>
      <h1>Shop Shirts</h1>
      <div className="shop-grid">
        {fullShirts.map((shirt, idx) => (
          <div
            key={idx}
            className="shirt-card"
            onClick={() => {
              setSelectedShirt(shirt);
              setPage("detail");
            }}
          >
            <img src={shirt.image} alt={shirt.name} />
            <h3>{shirt.name}</h3>
            <p>${shirt.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
