import React from "react";
import { loadStripe } from "@stripe/stripe-js";

export default function CheckoutButton({ itemName, amount }) {
  const handleCheckout = async () => {
    try {
      const res = await fetch("http://localhost:8000/create-checkout-session", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ item: itemName, amount }) // amount in dollars
      });

      const session = await res.json();

      if (session.error) {
        console.error(session.error);
        return;
      }

      // Use publishable key from backend
      const stripe = await loadStripe(session.publishableKey);
      const { error } = await stripe.redirectToCheckout({ sessionId: session.id });

      if (error) console.error(error);
    } catch (err) {
      console.error("Checkout error:", err);
    }
  };

  return (
    <button onClick={handleCheckout} className="button">
      Buy Now
    </button>
  );
}
